import parsecfg, streams
var p: CfgParser
let s = newStringStream("[Section]\nkey=val")
open(p, s, "test.ini")
while true:
  var e = next(p)
  if e.kind == cfgEof: break
  if e.kind == cfgKeyValuePair: echo e.key

